import { useEffect, useState } from "react";
import "./ImageAnimation.css";

const images = [
  'C:\Users\dedie\Downloads\0G2A4640פ.jpg',
  // 'https://7xl.com/wp-content/uploads/2022/10/bountyJackpot.png',
  // 'https://7xl.com/wp-content/uploads/2021/09/ggcare-ggcheers.png',
  // 'https://7xl.com/wp-content/uploads/2023/04/april-promo.png',
];

function ImageAnimation(): JSX.Element {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const intervalId = setInterval(() => {
      goToNext();
    }, 5000);

    return () => clearInterval(intervalId);
  }, [currentIndex]);

  const goToPrevious = () => {
    setCurrentIndex((currentIndex - 1 + images.length) % images.length);
  };

  const goToNext = () => {
    setCurrentIndex((currentIndex + 1) % images.length);
  };

  return (
    <div className="image-animation-container">
      {images.map((imageUrl, index) => (
        <img
          key={imageUrl}
          src={imageUrl}
          alt={`Image ${index + 1}`}
          className={`
            ${index === currentIndex ? 'active' : ''}
            ${index === currentIndex - 1 || (currentIndex === 0 && index === images.length - 1) ? 'previous' : ''}
            ${index === currentIndex + 1 || (currentIndex === images.length - 1 && index === 0) ? 'next' : ''}
          `}
        />
      ))}
      <button className="left" onClick={goToPrevious}>
        {"<"}
      </button>
      <button className="right" onClick={goToNext}>
        {">"}
      </button>
    </div>
  );
}

export default ImageAnimation;
